mapper.select:
select gugun_code as gugunCode, gugun_name as gugunName
from gugun
where sido_code = #{sidoCode}

mapper.update:
update hotplace
set count = count + 1
where content_id = #{id}

update hotplace
set count = count - 1
where content_id = #{id}

mapper.select:
select sido_name as sidoName, gugun_name as gugunName
from sido
         left join gugun
                   using (sido_code)
where sido_code = #{sidoCode}
  and gugun_code = #{gugunCode}

select count(*)
from likelist
where content_id = #{contentId}
  and user_id = #{userId}

mapper.delete:
delete
from likelist
where content_id = #{contentId}
  and user_id = #{userId}
